import React, { Component } from 'react';



class MainFloorType extends Component{

    state = {
        type: ''
    }

    handleInput(e){
        if  (e.target.value === "Residential")  this.props.skipStep();
        if  (e.target.value === "Commercial") this.props.nextStep();
        // console.log(e.target.value);
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }


    render(){

        return(
            <div>
                Residential/Commercial
                <button onClick={this.back}> Back </button>
                <button onClick={e => this.handleInput(e, "value")} value="Residential"> Residential </button>
                <button onClick={e => this.handleInput(e, "value")} value="Commercial"> Commercial </button>
            </div>
        )
    }

}

export default MainFloorType;
