import React, { Component } from 'react';



class MainFloorType extends Component{

    state = {
        hover: false
    }
    saveAndContinue(e){
        if  (e.target.value === "Residential")  this.props.skipStep();
        if  (e.target.value === "Commercial") this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    toggleHover=()=>{
        this.setState({
            hover: !this.state.hover 
        })

    }


    render(){

        return(
            <div>
                Residential/Commercial
                <button onClick={this.back}> Back </button>
                {/* <input type='text' onChange={this.props.handleChange('floorType')}></input> */}
                <input type="button" onClick={e => {this.saveAndContinue(e, "value"); this.props.handleButton(e)}} onMouseEnter={ this.toggleHover } onMouseLeave= { this.toggleHover } value={this.state.hover ? 'yes' : 'no'}/>
                <input type="button" onClick={e => {this.saveAndContinue(e, "value"); this.props.handleButton(e)}} onMouseEnter={ this.toggleHover } onMouseLeave= { this.toggleHover } value="Commercial"/>
            </div>
        )
    }

}

export default MainFloorType;
