import React, { Component } from 'react'
import PropTypes from 'prop-types'


export default class Image extends Component {

  render() {
    const { src, isSelected, onImageClick } = this.props
    const ImageStyle = (width, height) => {
      return {
        width,
        height,
        objectFit: "cover",
        border: `${isSelected ? '1px solid #499fb6': '1px solid white'}`
      }
    }
    return (
      <div className={`responsive${isSelected ? " selected" : ""}`}
        onClick={onImageClick}>
        <img 
            src={src}
            className={`thumbnail${isSelected ? " selected" : ""}`}

            style={ImageStyle(180, 180)}
            alt = "select-images"
        />
        <div className="checked">
          {/* <img src={imgCheck} alt="checkIcon" style={{ width: 75, height: 75, objectFit: "cover" }}/> */}
          <div className="icon"/>
        </div>
      </div>
    )
  }
}

Image.propTypes = {
  src: PropTypes.string,
  isSelected: PropTypes.bool
}
