import React, { Component } from 'react';

class MainFloorType extends Component{



    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    render(){
        const saveAndContinue = (val) => {
            console.log(val);
            if  (val === "Residential")  this.props.skipStep();
            if  (val === "Commercial") this.props.nextStep();
        }
        return(
            <div>
                Residential/Commercial
                <button onClick={this.back}> Back </button>
                <button onClick={saveAndContinue(this.value)} value="Residential"> Residential </button>
                <button onClick={saveAndContinue(this.value)} value="Commercial"> Commercial </button>
            </div>
        )
    }

}

export default MainFloorType;
