import React, { Component } from 'react';

class MainFloorType extends Component{

    state = {
        type: ''
    }

    saveAndContinue = () => {

        if  (this.state.type === "Residential")  this.props.skipStep();
        if  (this.state.type === "Commercial") this.props.nextStep();

    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    _handleClick =(val) =>{
        return function(e){
            e.preventDefault();
            setState(val);
        }
    }

    render(){

        return(
            <div>
                Residential/Commercial
                <button onClick={this.back}> Back </button>
                <button onClick={this._handleClick(this.value)} value="Residential"> Residential </button>
                <button onClick={this._handleClick(this.value)} value="Commercial"> Commercial </button>
            </div>
        )
    }

}

export default MainFloorType;
