import React, { Component } from 'react';


class FloorPlan extends Component{

    saveAndContinue = (e) =>{
        e.preventDefault()
        this.props.nextStep()

    }
    back  = (e) => {
        e.preventDefault();
        (this.props.values.floorType) === "Residential" ? this.props.prev2Step(): this.props.prevStep();
    }

    render(){
        return(
            <div>
                <div>
                    <label>1. Upload Floor Plan</label>
                    <input
                        type="file"
                        name = "floorPlanFile"
                        onChange={ this.props.handleChange('floorPlanFile') }
                    />
                </div>
                <div>
                    <label>1. Upload Floor Plan</label>
                    <input
                        type="text"
                        placeholder = "type in the purpose of your floor"
                        name = "commercialType"
                        onChange={ this.props.handleChange('commercialType') }
                    />
                </div>



                <button onClick={this.back}> Back </button>
                <button onClick={this.saveAndContinue}> Next </button>
            </div>
        )
    }
}

export default FloorPlan;
