import React, { Component } from 'react';

class MainFloorType extends Component{

    state = {
        type: ''
    }

    saveAndContinue = (val) => {

        this.setState({type: val });

        if  (this.state.type === "Residential")  this.props.skipStep();
        if  (this.state.type === "Commercial") this.props.nextStep();

    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }


    render(){

        return(
            <div>
                Residential/Commercial
                <button onClick={this.back}> Back </button>
                <button onClick={this.saveAndContinue(this.value)} value="Residential"> Residential </button>
                <button onClick={this.saveAndContinue(this.value)} value="Commercial"> Commercial </button>
            </div>
        )
    }

}

export default MainFloorType;
