import React, { Component } from 'react';
import Application from '../application/mainform';
import './_modal.scss'
import 'boxicons'

class Modal extends Component {


    onClose = (e) => {
        e.preventDefault();
        this.props.onClose && this.props.onClose(e);
    };

    render() {

        if(!this.props.show){
            return null;
        }
        return (
            <div className="modal">
                <Application />
                <button className="modal__closebtn" onClick={this.onClose}>Close</button>
            </div>
        )
    }
}
export default Modal;
