import React, { Component } from 'react';
import Application from '../application/mainform';


class Modal extends Component {

    modalStyle = {
        position: "absolute",
        top: "15%",
        left: "15%",
        width: "70%",
        height: "65%",
        zIndex: "30",
        backgroundColor: "#fdfdfd"
    }
    onClose = (e) => {
        e.preventDefault();
        this.props.onClose && this.props.onClose(e);
    };

    render() {

        if(!this.props.show){
            return null;
        }
        return (
            <div style={this.modalStyle} className="modal">
                <Application />
                <button className="modal__closebtn" onClick={this.onClose}>Close</button>
            </div>
        )
    }
}
export default Modal;
