import React, { Component } from 'react';
import Application from '../application/mainform';


class Modal extends Component {
    onClose = e => {
        this.props.show = false;
    };
  render() {
    if(!this.props.show){
        return null;
    }
    return (

        <div>
            <Application />
            <button onClose={e => {this.onClose(e);}}>Close</button>
        </div>



    )
  }
}
export default Modal;
