import React, { Component } from 'react';

class MainFloorType extends Component{
    state = {
        type: ''
    }
    saveAndContinue = () => {

        if  (this.state === "Residential")  this.props.skipStep();
        if  (this.state === "Commercial") this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    render(){

        return(
            <div>
                Residential/Commercial
                <button onClick={this.back}> Back </button>
                <button onClick={function(e){e.preventDefault();this.saveAndContinue(); this.setState(this.value)}} value="Residential"> Residential </button>
                <button onClick={function(e){e.preventDefault();this.saveAndContinue(); this.setState(this.value)}} value="Commercial"> Commercial </button>
            </div>
        )
    }

}

export default MainFloorType;
