import React, { Component } from 'react';
import MainTutorial from '../../tutorial/maintutorial'

class Tutorial extends Component{

    skipBtn ={
        position: "absolute",
        float: "right",
    }
    skip = (e) =>{
        e.preventDefault();
        this.props.nextStep();
    }

    render(){
        return(
            <div>
                <MainTutorial/>
                <button style = {this.skipBtn} onClick={this.skip}>
                    Skip
                </button>
            </div> 
        )
    }
}

export default Tutorial;
